<script>
import bus from "@/assets/js/eventBus.js";
export default {
  data() {
    return {
      // 是否显示弹出框
      modalShow: false,
      // 是否显示一部份导航栏
      isShow: true,
      // 是否显示管理系统
      isAdministration: true,
      // 弹出框的列表表头
      columns: [
        {
          title: "客服",
          key: "kefu",
          align: "center"
        },
        {
          title: "可放入公海池客户数量",
          key: "num",
          align: "center"
        },
        {
          title: "是否加入公海池",
          key: "action",
          align: "center",
          render: (h, params) => {
            return h(
              "div",
              {
                style: {
                  cursor: "pointer",
                  width: "100%",
                  height: "20px"
                },

                on: {
                  click: () => {
                    // 当前单元格客户的id
                    this.userId = params.row.id;
                  }
                }
              },
              "是"
            );
          }
        }
      ],
      // 列表数据
      tableData: [],
      // 列表高度
      tableHeight: 300
    };
  },

  mounted() {
    setTimeout(() => {
      // 得到浏览器内容高度
      var windowHeight =
        document.documentElement.clientHeight || document.body.clientHeight;
      this.tableHeight = windowHeight - 280;
    }, 100);
    // 调整浏览器的时候
    $(window).on("resize", () => {
      var windowHeight =
        document.documentElement.clientHeight || document.body.clientHeight;
      this.tableHeight = windowHeight - 280;
    });
    bus.$on("userDefinedEvent", message => {
      this.isShow = message;
    });
    bus.$on("customEvent", mes => {
      this.isAdministration = mes;
    });
  },
  computed: {
    // 权限管理
    qxlist() {
      return this.$root.qxlist;
    },
    // 监听当前页面路由变化，选中哪个菜单
    currentPath() {
      return this.$route.name;
    }
  },
  methods: {
    /**
     * 点击查看是否有BI这个功能
     */
    goVisualization() {
      this.$resetAjax({
        type: "POST",
        url: "/public/index.php?s=home/index/login",
        // root: '',
        root: "http://bi.xmvogue.com/",
        data: {
          userid: this.$root.userData.userid
        },
        success: res => {
          let result = JSON.parse(res);
          if (result.error === 0) {
            let routeData = this.$router.resolve({ name: "visualization" });
            window.open(routeData.href, "_blank");
          } else {
            this.$Modal.warning({
              title: "自动跳转",
              content: "您的登录已过期，点击确定，2秒后自动跳转到登录页面",
              onOk: () => {
                setTimeout(() => {
                  window.open(
                    "http://oa.xmvogue.com/index.php/NewA/Index/index/cs/Index",
                    "_self"
                  );
                }, 2000);
              }
            });
          }
        }
      });
    },
    goStoreData() {
      this.$resetAjax({
        type: "POST",
        url: "/public/index.php?s=home/index/login",
        // root: '',
        root: "http://bi.xmvogue.com/",
        data: {
          userid: this.$root.userData.userid
        },
        success: res => {
          let result = JSON.parse(res);
          if (result.error === 0) {
            let routeData = this.$router.resolve({ name: "areaData" });
            window.open(routeData.href, "_blank");
          } else {
            this.$Modal.warning({
              title: "自动跳转",
              content: "您的登录已过期，点击确定，2秒后自动跳转到登录页面",
              onOk: () => {
                setTimeout(() => {
                  window.open(
                    "http://oa.xmvogue.com/index.php/NewA/Index/index/cs/Index",
                    "_self"
                  );
                }, 2000);
              }
            });
          }
        }
      });
    },
    get() {
      this.$resetAjax({
        url: "/NewA/Customer/listinit",
        type: "POST",
        data: {
          yeah: 1
        },
        success: res => {
          if (JSON.parse(res).errorcode == 0) {
            this.$root.tip.isShow = true;
            this.$root.tip.content = "成功加入公海池";
            setTimeout(() => {
              this.$root.tip.isShow = false;
            }, 1500);
          } else {
            this.$root.tip.isShow = true;
            this.$root.tip.content = "加入公海池失败";
            setTimeout(() => {
              this.$root.tip.isShow = false;
            }, 1500);
          }
        }
      });
    },
    goCRM() {
      this.$router.push({ path: "customer" });
      this.$resetAjax({
        url: "/NewA/Customer/listinit",
        type: "GET",
        success: res => {
          if (
            JSON.parse(res).user == "刘翔" &&
            JSON.parse(res).data.length > 0
          ) {
            this.modalShow = true;
            this.tableData = JSON.parse(res).data;
          } else if (JSON.parse(res).data.length <= 0) {
            this.modalShow = false;
          } else {
            let content = `您今天有【${
              JSON.parse(res).data
            }位】客户需要回访，有【${
              JSON.parse(res).count
            }位】客户还未回访过！`;
            this.$Modal.warning({
              title: "注意查看",
              content: content,
              onOk: () => {
                bus.$emit("eventName");
              }
            });
          }
        }
      });
    },
    active() {
      this.$resetAjax({
        url: "/NewA/Public/checklogin",
        type: "POST",
        success: res => {
          if (JSON.parse(res).msg == "error") {
            this.$Modal.warning({
              title: "自动跳转",
              content: "您的登录已过期，点击确定，2秒后自动跳转到登录页面",
              onOk: () => {
                setTimeout(() => {
                  window.open(
                    "http://oa.xmvogue.com/index.php/NewA/Index/index/cs/Index",
                    "_self"
                  );
                }, 2000);
              }
            });
          }
        }
      });
    }
    // 跳转到之前的OA系统
    // goOld() {
    //     window.open('http://oa.xmvogue.com/index.php/Home/Index/main');
    // }
  }
};
</script>

